pragma solidity ^0.8.19; 

import "@openzeppelin/contracts/utils/Context.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "../common/AccessControl.sol";

struct AppStorage {
	// Access Control Enumerable
	mapping(bytes32 => RoleData) _roles;
	string failsafe;
	mapping(bytes32 => EnumerableSet.AddressSet) _roleMembers;
	// App
	address[] creators;
	mapping(address => address[]) creatorToContracts;
	mapping(address => address) contractToCreator;
	mapping(address => uint) deploymentCostForToken;
	// Always add new variables at the end of the struct!
}

library LibAppStorage {
	function diamondStorage() internal pure	returns (AppStorage storage ds) {
		assembly {
			ds.slot := 0
		}
	}
}

/// @title 	An Access Control contract
/// @notice You can use this contract to modify and define the role of an user
contract AccessControlAppStorageEnumerable is Context, AccessControlEnumerable {
	using EnumerableSet for EnumerableSet.AddressSet;

	AppStorage internal s;

	/// @notice This function allow us to know if an user has or not an specific role
	/// @dev 	Notice that this function override the behavior of
	/// @dev 	the hasrole function inherited from AccessControlEnumerable
	/// @param 	role Contains the role we want to verify
    /// @param 	account Contains the address to match with the verification 
	/// @return bool Answer if the account has the input role or not
	function hasRole(bytes32 role, address account) public view override returns (bool) {
		return s._roles[role].members[account];
	}

	/// @notice Allows us to see what role is available to modify with the selected role
	/// @dev 	Notice that this function override the behavior of
	/// @dev 	the getRoleAdmin function inherited from AccessControlEnumerable
	/// @param 	role Contains the role that we want to check
	/// @return bytes32 the rol that in available to change
	function getRoleAdmin(bytes32 role) public view override returns (bytes32) {
		return s._roles[role].adminRole;
	}

	/// @notice Allow us to use an index position to verify the account that has a role
	/// @dev 	Notice that this function override the behavior of
	/// @dev 	the getRoleMember function inherited from AccessControlEnumerable
	/// @param 	role Contains an specific role to check
    /// @param 	index Contains the position of the array that we want to verify
	/// @return address of the account with that position of the list of the selected role
	function getRoleMember(bytes32 role, uint256 index) public view override returns (address) {
		return s._roleMembers[role].at(index);
	}

	/// @notice Allow us to know the total of members that has a role
	/// @dev 	Notice that this function override the behavior of
	/// @dev 	the getRoleMemberCount function inherited from AccessControlEnumerable
	/// @param 	role Contains an specific role to check
	/// @return uint256 answer the cuantity of accounts with that role
	function getRoleMemberCount(bytes32 role) public view override returns (uint256) {
		return s._roleMembers[role].length();
	}

	/// @notice Allow us to use to set a new admin role
	/// @dev 	Notice that this function override the behavior of
	/// @dev 	the _setRoleAdmin function inherited from AccessControlEnumerable
	/// @param 	role Contains an specific role to check
    /// @param 	adminRole Contains the new admin role we want to provide
	function _setRoleAdmin(bytes32 role, bytes32 adminRole) internal override {
		bytes32 previousAdminRole = getRoleAdmin(role);
		s._roles[role].adminRole = adminRole;
		emit RoleAdminChanged(role, previousAdminRole, adminRole);
	}

	/// @notice Allow us to grant a new role to an account
	/// @dev 	Notice that this function override the behavior of
	/// @dev 	the _grantRole function inherited from AccessControlEnumerable
	/// @param 	role Contains the facet addresses and function selectors
    /// @param 	account Contains the facet addresses and function selectors
	function _grantRole(bytes32 role, address account) internal override {
		if (!hasRole(role, account)) {
			s._roles[role].members[account] = true;
			emit RoleGranted(role, account, _msgSender());
			s._roleMembers[role].add(account);
		}
	}

	/// @notice Allow us to revoke a role of an account
	/// @dev 	Notice that this function override the behavior of
	/// @dev 	the _revokeRole function inherited from AccessControlEnumerable
	/// @param 	role Contains the facet addresses and function selectors
    /// @param 	account Contains the facet addresses and function selectors
	function _revokeRole(bytes32 role, address account) internal override {
		if (hasRole(role, account)) {
			s._roles[role].members[account] = false;
			emit RoleRevoked(role, account, _msgSender());
			s._roleMembers[role].remove(account);
		}
	}
}","keccak256":"0xb4e04ebe7a7d26e3bbc5fa9141094783a306c846f26c54da98b3c0460092c99e","license":"GPL-3.0"},"contracts/Factory/facets/ERC777ReceiverFacet.sol":{"content":"// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.19; 

import "@openzeppelin/contracts/token/ERC777/IERC777.sol";
import "@openzeppelin/contracts/token/ERC777/IERC777Recipient.sol";
import '../AppStorage.sol';
import '../../tokens/RAIR-ERC721-Diamond.sol';

/// @title 	Our Facet creator for ERC777 contracts
/// @notice You can use this contract to deploy new ERC777 contracts
contract ERC777ReceiverFacet is IERC777Recipient, AccessControlAppStorageEnumerable {
	bytes32 constant ERC777 = keccak256("ERC777");
	bytes32 constant OWNER = keccak256("OWNER");

	/// @notice This event stores in the blockchain when a new contract is deployed
    /// @param	deployerAddress Contains the address of the deployer of the contract 
    /// @param 	deploymentIndex Contains the corresponding ID for the deployment
    /// @param 	deploymentAddress Contains the address where the contract was deployed
	/// @param 	deploymentName Contains the name of the deployed contract
	event DeployedContract(address deployerAddress, uint deploymentIndex, address deploymentAddress, string deploymentName);

	/// @notice Function called by an ERC777 when they send tokens
	/// @dev   	This is our deployment mechanism for ERC721 contracts!
	/// @param 	operator The ERC777 operator calling the send() function
	/// @param 	from The owner of the tokens
	/// @param 	to The recipient of the tokens
	/// @param 	amount The number of tokens sent
	/// @param 	userData bytes sent from the send call
	/// @param 	operatorData bytes sent from the operator
	function tokensReceived(address operator, address from, address to, uint256 amount, bytes calldata userData, bytes calldata operatorData) external onlyRole(ERC777) override {
		AppStorage storage s = LibAppStorage.diamondStorage();
		require(amount >= s.deploymentCostForToken[msg.sender], 'RAIR Factory: not enough RAIR tokens to deploy a contract');

		if (amount - (s.deploymentCostForToken[msg.sender]) > 0) {
			IERC777(msg.sender).send(from, amount - (s.deploymentCostForToken[msg.sender]), bytes("RAIR"));
		}
		address[] storage deploymentsFromOwner = s.creatorToContracts[from];

		if (deploymentsFromOwner.length == 0) {
			s.creators.push(from);
		}

		RAIR_ERC721_Diamond newToken = new RAIR_ERC721_Diamond(string(userData), from, 30000);
		deploymentsFromOwner.push(address(newToken));
		s.contractToCreator[address(newToken)] = from;
		emit DeployedContract(from, deploymentsFromOwner.length - 1, address(newToken), string(userData));
	}
}","keccak256":"0x20e1162f10915eabaaeb5685e8542027da3eed0120ec57beec2154d575987082","license":"GPL-3.0"},"contracts/common/AccessControl.sol":{"content":"// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.19; 

import "@openzeppelin/contracts/utils/Context.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";

struct RoleData {
	mapping(address => bool) members;
	bytes32 adminRole;
}

/// @title  A contract that administrate roles & access
/// @notice You can use this contract to modify and define the role of an user
abstract contract AccessControlEnumerable is Context {	
    /// @notice This event stores in the blockchain when an admin role changes
    /// @param  role Contains the admin role that we want to use 
    /// @param  previousAdminRole Contains the previous admin role
    /// @param  newAdminRole Contains the new admin role
	event RoleAdminChanged(bytes32 indexed role, bytes32 indexed previousAdminRole, bytes32 indexed newAdminRole);
	/// @notice This event stores in the blockchain when a role is granted
    /// @param  role Contains the admin role that we want to use 
    /// @param  account Contains the account we want to add to a new role
    /// @param  sender Contains the sender of the role petition
    event RoleGranted(bytes32 indexed role, address indexed account, address indexed sender);
    /// @notice This event stores in the blockchain when a role is revoked
    /// @param  role Contains the admin role that we want to use 
    /// @param  account Contains the account we want to add to a new role
    /// @param  sender Contains the sender of the role petition
    event RoleRevoked(bytes32 indexed role, address indexed account, address indexed sender);

    modifier onlyRole(bytes32 role) {
        _checkRole(role, _msgSender());
        _;
    }

    /// @notice Allow an user to quit an owned role
    /// @notice The account that sends the petition needs to be the same that will renounce to a role
    /// @param  role Contains the role that we want to use 
    /// @param  account Contains the account address to use.    
    function renounceRole(bytes32 role, address account) public {
        require(account == _msgSender(), "AccessControl: can only renounce roles for self");
        _revokeRole(role, account);
    }

    /// @notice Allow an admin to asign a new role to an account
    /// @param  role Contains the role that we want to use 
    /// @param  account Contains the account address to use. 
    function grantRole(bytes32 role, address account) public onlyRole(getRoleAdmin(role)) {
        _grantRole(role, account);
    }

    /// @notice Allow an admin to revoke a role to an account
    /// @param  role Contains the role that we want to use 
    /// @param  account Contains the account address to use. 
    function revokeRole(bytes32 role, address account) public onlyRole(getRoleAdmin(role)) {
        _revokeRole(role, account);
    }

    /// @notice Allow to verify if the account has a role
    /// @param  role Contains the role that we want to use 
    /// @param  account Contains the account address to use. 
    function _checkRole(bytes32 role, address account) internal view {
        if (!hasRole(role, account)) {
            revert(
                string(
                    abi.encodePacked(
                        "AccessControl: account ",
                        Strings.toHexString(uint160(account), 20),
                        " is missing role ",
                        Strings.toHexString(uint256(role), 32)
                    )
                )
            );
        }
    }

    /// @notice Allow to verify if the account has a role
    /// @param  role Contains the role that we want to verify 
    /// @param  account Contains the account address to check. 
    /// @return role in boolean, if the account has the selected role
    function hasRole(bytes32 role, address account) public view virtual returns (bool);

	/// @notice Allow us to verify the branch of roles asociated to an father role
    /// @param  role Contains the role that we want to verify
    /// @return bytes32 with the child role
    function getRoleAdmin(bytes32 role) public view virtual returns (bytes32);

    /// @notice Check if the account with the index has the desired role
    /// @param  role Contains the role that we want to use 
    /// @param  index Contains the index asociated to an account
    /// @return address of the account with the index position in the list of the desired role
	function getRoleMember(bytes32 role, uint256 index) public view virtual returns (address);

    /// @notice Allow to verify if the account has a role
    /// @param  role Contains the role that we want to verify
    /// @return uint256 wuth he total of members with the desired role 
	function getRoleMemberCount(bytes32 role) public view virtual returns (uint256);

    /// @param role Contains the role that we want to use 
    /// @param adminRole Contains the new admin role to use
	function _setRoleAdmin(bytes32 role, bytes32 adminRole) internal virtual;

    /// @notice Grants a role to an account
    /// @param  role Contains the role that we want to use 
    /// @param  account Contains the account address to use. 
	function _grantRole(bytes32 role, address account) internal virtual;

    /// @notice Revokes a role to an account 
    /// @param  role Contains the role that we want to use 
    /// @param  account Contains the account address to use. 
	function _revokeRole(bytes32 role, address account) internal virtual;
}","keccak256":"0xdaebcf9440203930cebc6e8decfbddac015b0fda8ebd26b18d6bb8d6af0ffeee","license":"GPL-3.0"},"contracts/diamondStandard/interfaces/IDiamondLoupe.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

/******************************************************************************\
* Author: Nick Mudge <nick@perfectabstractions.com> (https://twitter.com/mudgen)
* EIP-2535 Diamonds: https://eips.ethereum.org/EIPS/eip-2535
/******************************************************************************/

// A loupe is a small magnifying glass used to look at diamonds.
// These functions look at diamonds
interface IDiamondLoupe {
    /// These functions are expected to be called frequently
    /// by tools.

    struct Facet {
        address facetAddress;
        bytes4[] functionSelectors;
    }

    /// @notice Gets all facet addresses and their four byte function selectors.
    /// @return facets_ Facet
    function facets() external view returns (Facet[] memory facets_);

    /// @notice Gets all the function selectors supported by a specific facet.
    /// @param _facet The facet address.
    /// @return facetFunctionSelectors_
    function facetFunctionSelectors(address _facet) external view returns (bytes4[] memory facetFunctionSelectors_);

    /// @notice Get all the facet addresses used by a diamond.
    /// @return facetAddresses_
    function facetAddresses() external view returns (address[] memory facetAddresses_);

    /// @notice Gets the facet that supports the given selector.
    /// @dev If facet is not found return address(0).
    /// @param _functionSelector The function selector.
    /// @return facetAddress_ The facet address.
    function facetAddress(bytes4 _functionSelector) external view returns (address facetAddress_);
}
